#!/bin/sh
set -e
echo "ðŸ“¤ Posting incident report to Slack"

# Extract Slack token from pre-fetched secrets
SLACK_API_KEY=$(echo "$all_secrets" | jq -r '.SLACK_API_KEY')

if [ "$SLACK_API_KEY" = "null" ] || [ -z "$SLACK_API_KEY" ]; then
    echo "âŒ Slack API key not available in pre-fetched secrets"
    exit 1
fi

# Post the report
curl -s -X POST "https://slack.com/api/chat.postMessage" \
  -H "Authorization: Bearer $SLACK_API_KEY" \
  -H "Content-Type: application/json" \
  -d "{
    \"channel\": \"$channel_id\",
    \"text\": \"ðŸ“‹ Incident Report Complete\",
    \"blocks\": [
      {
        \"type\": \"section\",
        \"text\": {
          \"type\": \"mrkdwn\",
          \"text\": \"$report_content\"
        }
      },
      {
        \"type\": \"context\",
        \"elements\": [
          {
            \"type\": \"mrkdwn\",
            \"text\": \"ðŸ¤– Generated by Claude Code Incident Response Workflow â€¢ $(date)\"
          }
        ]
      }
    ]
  }" > /dev/null

echo "âœ… Incident report posted to Slack"
